import React, { useEffect, useState } from "react";
import cartData from "./components/CartData.json";
import style from "./CartList.module.css";

export const CartList = () => {
  const [items, setItems] = useState([]);
  const [initialValue, setInitialValue] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    setInitialValue(cartData);
    let total = 0;
    cartData.map((data) => {
      data.discountNo = ((data.mrp * data.discount) / 100).toFixed();
      data.total = data.mrp - data.discountNo;
      total += data.total;
    });
    setTotalPrice(total);
    setItems(cartData);
  });

  const handleQtyChange = (value, item) => {
    const cart = initialValue.find((c) => c.id === item.id);

    let total = totalPrice;
    items.forEach((itm) => {
      if (itm.id === item.id) {
        itm.mrp = cart.mrp * value.target.value;

        itm.qty = value.target.value;
        itm.discountNo = ((itm.mrp * itm.discount) / 100).toFixed();
        total -= itm.total;
        itm.total = itm.mrp - itm.discountNo;
        total += itm.total;
      }
    });
    setTotalPrice(total);
    setItems(items);
  };

  console.log("item : ", items);
  console.log("cartData : ", cartData);

  return (
    <>
      <span>{`You currently have ${items.length} items(s) in your cart`}</span>
      <table className={style.table}>
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity</th>
            <th>
              MRP<sub>(per Unit)</sub>
            </th>
            <th>Discount</th>
            <th>Total</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr>
              <td>
                <div style={{ display: "flex" }}>
                  <div>
                    <img className={style.mattress} src={item.thumbnail} />
                  </div>
                  <div style={{ marginLeft: "5%" }}>
                    <div>{item.title}</div>
                    <div
                      style={{ fontSize: "14px", color: "grey" }}
                    >{`${item.skucode} - ${item.name}`}</div>
                  </div>
                </div>
              </td>
              <td>
                <select
                  name="qty"
                  id="qty"
                  style={{ padding: "5%" }}
                  onChange={(value) => handleQtyChange(value, item)}
                >
                  <option value={1}>1</option>
                  <option value={2}>2</option>
                  <option value={3}>3</option>
                  <option value={4}>4</option>
                  <option value={5}>5</option>
                </select>
              </td>
              <td>{item.mrp}</td>
              <td>{item.discountNo}</td>
              <td>{item.total}</td>
              <td></td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3
        style={{ textAlign: "right", marginRight: "10%" }}
      >{`Total ${totalPrice}`}</h3>
    </>
  );
};
