import React, { useEffect, useState } from "react";
import { cartData } from "./components/CartData";
import style from "./CartList.module.css";

export const CartList = () => {
  const [items, setItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    let total = 0;
    cartData.map((data) => {
      data.discountNo = ((data.mrp * data.discount) / 100).toFixed();
      data.total = data.mrp - data.discountNo;
      total += data.total;
    });
    setTotalPrice(total);
    setItems(cartData);
  });

  return (
    <>
      <span>{`You currently have ${items.length} items(s) in your cart`}</span>
      <table className={style.table}>
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity</th>
            <th>
              MRP<sub>(per Unit)</sub>
            </th>
            <th>Discount</th>
            <th>Total</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr>
              <td>
                <div style={{ display: "flex" }}>
                  <div>
                    <img className={style.mattress} src={item.thumbnail} />
                  </div>
                  <div style={{ marginLeft: "5%" }}>
                    <div>{item.title}</div>
                    <div
                      style={{ fontSize: "14px", color: "grey" }}
                    >{`${item.skucode} - ${item.name}`}</div>
                  </div>
                </div>
              </td>
              <td>
                <select>{item.qty}</select>
                <select name="cars" id="cars">
                  <option value="volvo">Volvo</option>
                  <option value="saab">Saab</option>
                  <option value="mercedes">Mercedes</option>
                  <option value="audi">Audi</option>
                </select>
              </td>
              <td>{item.mrp}</td>
              <td>{item.discountNo}</td>
              <td>{item.total}</td>
              <td></td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3
        style={{ textAlign: "right", marginRight: "10%" }}
      >{`Total ${totalPrice}`}</h3>
    </>
  );
};
